import axios from "axios";
import x2js from "x2js";
export default {
  name: "LK",

  data() {
    return {
      data_isshow: false,
      data: "",
      resdata_json: []
    };
  },

  methods: {
    readAsText(params) {
      let file = params.file;
      let reader = new FileReader();
      reader.readAsText(file);

      reader.onload = function () {
        let text = reader.result;
        this.data = text;
        console.log(this.data);
        this.success("文件读取成功");
        axios.post('/api//LKMetrics', this.data, {
          headers: {
            'Content-Type': 'application/xml'
          }
        }).then(response => {
          console.log(response);
          var xml_str = response.data.toString();
          console.log(xml_str);
          const myx2json = new x2js(); //实例

          const json = myx2json.xml2js(xml_str); //解析

          console.log(json);
          let that = this;
          this.resdata_json = [];
          json.message.lkResultList.result.forEach(function (item) {
            console.log('this', this);
            console.log('that', that);
            that.resdata_json.push(item);
          });
          this.data_isshow = true;
        }).catch(error => {
          console.log(error);
          this.warn("文件内容不支持");
          this.data_isshow = false;
        });
      }.bind(this);
    },

    xmlToJson(xml) {
      // Create the return object
      var obj = {};

      if (xml.nodeType == 1) {
        // element
        // do attributes
        if (xml.attributes.length > 0) {
          obj["@attributes"] = {};

          for (var j = 0; j < xml.attributes.length; j++) {
            var attribute = xml.attributes.item(j);
            obj["@attributes"][attribute.nodeName] = attribute.nodeValue;
          }
        }
      } else if (xml.nodeType == 3) {
        // text
        obj = xml.nodeValue;
      } // do children


      if (xml.hasChildNodes()) {
        for (var i = 0; i < xml.childNodes.length; i++) {
          var item = xml.childNodes.item(i);
          var nodeName = item.nodeName;

          if (typeof obj[nodeName] == "undefined") {
            obj[nodeName] = this.xmlToJson(item);
          } else {
            if (typeof obj[nodeName].push == "undefined") {
              var old = obj[nodeName];
              obj[nodeName] = [];
              obj[nodeName].push(old);
            }

            obj[nodeName].push(this.xmlToJson(item));
          }
        }
      }

      return obj;
    },

    success(message) {
      this.$message({
        message: message,
        type: 'success'
      });
    },

    warn(message) {
      this.$message({
        message: message,
        type: 'warning'
      });
    }

  }
};
